package com.kaizen.operators;

public class Ifelse {

	public static void main(String[] args) {
		int a=100;
		int b=20;
		int c=30;
		if (a>b && a>c) { 
			System.out.println("a is winner");
		}
		else if(b>c && b>a)
		{
				System.out.println("b is winner");
		 }
		else
		{
			System.out.println("c is winner");
			
		}
		}
}			
		
